INSERT INTO `komentar` (`id_komentar`, `id_artikel`, `nama`, `email`, `isi_komentar`, `status_komentar`) VALUES
(9, 178, 'Kim Taehyung', 'kimtaetae95@gmail.com', 'Candi prambanan merupakan wahana wisata edukasi sejarah yang sangat keren. Akses keluar candi bagi wisatawan yaitu dengan berjalan kaki. Siapkan tenaga jika akan keluar sewaktu datang.', 1),
(10, 178, 'Na Jaemin', 'jaeminana13@gmail.com', 'Candi prambanan bagus dan keren, banyak turist yang datang. Recomended banget buat kalian yang pergi ke Jogja untuk datang kesini...', 1),
(11, 164, 'Lucas Wong', 'huangxuxi99@gmail.com', 'Rame dan seru banget tempatnya, kalo mau foto-foto harus hati-hati banget dengan kendaraan yang berlalu lalang, karena terletak di tengah-tengah perempatan besar pusat kota jogja. Jadi ambil fotonya tunggu lampu merah..', 1),
(12, 164, 'Haechan', 'leedonghyuck06@gmail.com', 'Tempat favorit untuk nongkrong, ga pernah bosan ke sini, tempat yang wajib dikunjungi.', 1),
(13, 163, 'Kim Namjoon', 'rapmonster94@gmail.com', 'Tempat ini menyimpan banyak sekali miniatur, sejarah, bacaan dinding dan benda asli dari perjuangan indonesia melawan penjajah. Bahkan ada sejarah mata uang indonesia. Sangat direkomendasikan untuk berkunjung kesini. Cocok sekali untuk mengajak anak belajar nilai-nilai perjuangan. Selamat berkunjung! And have a nice trip!', 1),
(14, 177, 'Yoon Jeong Han', 'jeonghan04@gmail.com', 'Luar biasa, Recommend....!!! Jangan lupa pakai jasa pemandu wisata ya biar paham akan sejarah dan legenda yang ada di situs candi ini.', 1),
(15, 177, 'Jeon Wonwoo', 'jeonwonwoo17@gmail.com', 'Salah satu tempat favorit untuk lihat sunset. Bersih sekali, udaranya juga bagus.', 1),
(16, 177, 'S.Coups', 'choiseungcheol08@gmail.com', 'Destinasi wisata yang perlu dijaga dan dilestarikan. Cocok untuk wisata edukasi keluarga. Viewnya bagus, banyak spot photo cantik sebagai kenangan. Lingkungannya bersih dan tenang, walau di tempat terbuka tapi udaranya tidak terlalu panas, karena letaknya di atas tebing tinggi.', 1),
(17, 221, 'Suga', 'minyoongi93@gmail.com', 'Nyaman untuk rekreasi keluarga. Spot foto yg bagus. Rekomendasi datang saat sunrise dan sunset.', 1),
(18, 116, 'Kim Mingyu', 'kiming06@gmail.com', 'Tempat masih sangat alami, fasilitas sudah cukup lengkap, ada area parkir, beberapa warung makan sederhana yag menyewakan ban pelampung. Disarankan ke tempat ini pagi-pagi. Selamat berkunjung:)', 1),
(19, 119, 'Jeon Jungkook', 'jungkookiee97@gmail.com', 'Punya pengalaman lucu dan unik disini! Aku dan temen-temen bermain sandboarding. Cukup susah awalnya ketika bermain, jatuh muluu tapi ga sakit, yaa kaya jatuh di kasur haha. Kalo yg masih takut main sandboarding duduk aja di atas papannya dan meluncur deh, jadi kaya perosotan. Kesini tuh enaknya pagi banget atau sore jam 4-an.', 1),
(20, 119, 'Liu Yangyang', 'yangyang1010@gmail.com', 'Tempat wisata unik yg wajib dikunjungi kalau lagi ke jogja. Lokasinya tidak jauh dari pantai parangkusumo. Serasa di timur tengah, bukit pasir yg panas. Recomended spot foto pagi hari jam 06.00 sd 08.30, kalau sore sekitar jam 16.00 sd 17.30.', 1),
(21, 121, 'Dino', 'leechan11@gmail.com', 'Perjalanan menuju gua jomblang memang sempit dan berbatu, tapi worth to visit banget gua jomblang ini. Buat yg mau kesini harus siap baju ganti, pakaian yg nyaman dan harus siap kotor karena di dalam gua berlumpur, apalagi saat musim hujan. Guide nya sangat ramah, mau bantu ambil foto dan sudah jelas tau spot yg bagus nya dimana aja.', 1),
(22, 175, 'Jun', 'wenjunhui96@gmail.com', 'Candi hindu yg unik karena letaknya ada dibawah permukaan tanah. Candi tergolong kecil dan berdiri di atas lahan yg tidak begitu luas, tapi sangat menarik.', 1),
(23, 174, 'Hoshi', 'hoshii1505@gmail.com', 'Candi barong ini lokasinya memang agak terpencil tapi bagus banget pemandangannya.', 1),
(24, 174, 'DK', 'leeseokmin18@gmail.com', 'Tempat yg bagus, cocok untuk melihat matahari terbenam. Tempatnya juga bersih dan terawat.', 1),
(25, 173, 'Seungkwan', 'seungkwan1601@gmail.com', 'Wisata sejarah di daerah klasan klaten, lokasinya mudah di akses dari jalan Solo Jogja, sangat mudah dicari dan ditemukan.', 1),
(26, 171, 'Joshua', 'joshuahong30@gmail.com', 'Di Museum Perjuangan ini kita disuguhkan dengan sejarah pergerakan menuju kemerdekaan Indonesia. Salah satu Museum yg sangat saya rekomendasikan untuk anda kunjungi.', 1),
(27, 171, 'Jeno', 'leejeno04@gmail.com', 'Tempatnya unik dan nyaman, bisa memenuhi rasa keingintahuan akan sejarah perjuangan bangsa.', 1),
(31, 166, 'Renjun', 'huangrenjun23@gmail.com', 'Pengalaman yg seru, belajar membatik, hasilnya bisa di bawa pulang...', 1),
(32, 166, 'Jisung', 'parkjisung05@gmail.com', 'Salah satu yang aku seneng banget, waktu ke museum ini, selain menambah pengetahuan tentang batik dan perkembangannya, aku bisa mengikuti workshopnya, jadi aku diajarin dan praktek langsung proses membatik dari awal sampai akhir.', 1),
(34, 168, 'Chenle', 'zhongchenle22@gmail.com', 'Menyenangkan bisa berkunjung kesini. Museumnya adem tempatnya nyaman pemandunya semangat banget jelasinnya.', 1),
(35, 170, 'J-Hope', 'junghoseok18@gmail.com', 'Salah satu tempat bersejarah di Yogyakarta. Penuh dengan sejarah perjuangan Indonesia saat masa lalu. Bila beruntung pengunjung dapat menyaksikan pertunjukan seni.', 1),
(36, 169, 'Park Jimin', 'chimchim13@gmail.com', 'Museum yg bagus untuk dikunjungi. Perpaduan pajangan benda-benda sejarah dengan teknologi.', 1),
(37, 169, 'Kim Seokjin', 'seokjin04@gmail.com', 'Museum ini bagus untuk dijadikan wisata edukasi bagi teman-teman yang ingin tahu budaya jawa.\r\nAnda bisa masuk dan melihat-lihat benda-benda peninggalan yg ada di dalemnya. Ada juga ruang pengenalan, ruang pra sejarah, ruang perkembangan agama di jawa, ada ruang wayang, dll.', 1);

